import { React, useState } from "react";
import PlayerInfo from "./PlayerInfo";
import TeamInfo from "./TeamInfo";

function Search() {
  const [playersShown, setPlayersShown] = useState("Players");
  const handleClick = (e) => {
    setPlayersShown(e.target.value);
  };

  return (
    <div>
      <nav className="flex">
        <div className="flex-auto">
          <input
            className="sr-only peer"
            type="radio"
            name="answer"
            value="Players"
            id="answer_players"
            checked={playersShown === "Players"}
            onClick={handleClick}
          />
          <label
            for="answer_players"
            className="peer-checked:border-green-500 peer-checked:border-2 basis-full"
          >
            Players
          </label>
        </div>
        <div className="flex-auto">
          <input
            className="sr-only peer "
            type="radio"
            name="answer"
            value="Teams"
            id="answer_teams"
            checked={playersShown === "Teams"}
            onClick={handleClick}
          />
          <label
            for="answer_teams"
            className="peer-checked:border-green-500 peer-checked:border-2"
          >
            Teams
          </label>
        </div>
      </nav>
      <div>{playersShown == "Players" && <PlayerInfo />}</div>
      <div>{playersShown == "Teams" && <TeamInfo />}</div>
    </div>
  );
}

export default Search;
