import React from "react";
import PlayerInfo from "./PlayerInfo";
import TeamInfo from "./TeamInfo";

function Search() {
  const [playersShown, setPlayersShown] = useState(true);
  function handleClick() {
    setPlayersShown((current) => !current);
  }

  return (
    <div>
      <nav className="flex">
        <div className="flex-auto">
          <input
            className="sr-only peer"
            type="radio"
            name="answer"
            value="Players"
            id="answer_players"
            // onClick={handleClick}
          />
          <label
            for="answer_players"
            className="peer-checked:border-green-500 peer-checked:border-2 basis-full"
          >
            Players
          </label>
        </div>
        <div className="flex-auto">
          <input
            className="sr-only peer "
            type="radio"
            name="answer"
            value="Teams"
            id="answer_teams"
            // onClick={handleClick}
          />
          <label
            for="answer_teams"
            className="peer-checked:border-green-500 peer-checked:border-2"
          >
            Teams
          </label>
        </div>
      </nav>
      <div>{playersShown && <PlayerInfo />}</div>
      <div>{!playersShown && <TeamInfo />}</div>
    </div>
  );
}

export default Search;
