import React, {useState, useEffect} from 'react';
import axios from "axios";

function TodayBoxScores() {
  const [games, setGames] = useState([]);

  useEffect(() => {
    const theUrl = '/api/games/boxscores'
    axios.get(theUrl, {
    }).then(res => {
      setGames(res.data)
    }).catch(err => {
      console.log(err)
    })
  }, [])

  return (
    <div>
      {/* {
        games.map(({ awayID, awayName, awayScore, id, homeID, homeName, homeScore, status }) => (
        <div key={id}>
            <div>Status: {status}</div>
            <div>Home Team: {homeName}({homeID}) - {homeScore}</div>
            <div>Away Team: {awayName}({awayID}) - {awayScore}</div>
            <div>_____________</div>
          </div>
        ))
      } */}
      {
        games.map(game => (
        <div key={game.game_id}>
            <div>Status: {game.status}</div>
            <div>Home Team: {game.home_team_name}({game.home_team_id}) - {game.home_team_score}</div>
            <div>Away Team: {game.away_team_name}({game.away_team_id}) - {game.away_team_score}</div>
            <div>_____________</div>
          </div>
        ))
      }
    </div>
  );
}

export default TodayBoxScores;

